mapper.select:
SELECT @ROWNUM:=@ROWNUM+1 AS RNUM , contractIdx , exportType , documentNumber , contractAmount , currencyCode , DATE_FORMAT(contractDateTime, '%Y.%m.%d %H:%i:%s') AS contractDateTime , priceTerms , DATE_FORMAT(shipmentDueDateTime, '%Y.%m.%d %H:%i:%s') AS shipmentDueDateTime , negoAmount , balanceAmount , exporterName , exporterSsn , exporterAddress , importerName , importerCountryCode , importerAddress , shipmentCountryCode , shipmentPortCode , shipmentPortName , transShipmentCountryCode , transShipmentPortCode , transShipmentPortName , destinationCountryCode , destinationPortCode , destinationPortName , DATE_FORMAT(regDate, '%Y.%m.%d %H:%i:%s') AS regDate FROM export_contract A, (SELECT @ROWNUM:=#{from_cnt}) R WHERE 1=1
AND documentNumber = #{documentNumber}
AND exportType = #{exportType}
AND regDate between #{startDateTime} and #{endDateTime}
ORDER BY regDate DESC
limit #{from_cnt}, #{to_cnt}

			SELECT
			    COUNT(*)
 			FROM
				export_contract
			WHERE 1=1 
			
				AND documentNumber = #{documentNumber}
			
			
				AND exportType = #{exportType}
			
			
				AND regDate between #{startDateTime} and #{endDateTime}
